import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import { Link } from "react-router-dom"
import "./ExerciseActivity.css"

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    marginBottom: 12,
  },
  createdDetails: {
    fontSize: 14,
  },
});

export default function ExerciseActivity({ activity, user }) {
  const classes = useStyles();
  const userOwnsActivityEntry = user?.id && activity?.users_id === user?.id
  return (
    <div className="ExerciseActivity">
      <h1> {activity.name}</h1>
      <Typography className={classes.createdDetails} color="textSecondary" gutterBottom>
          Created At
        </Typography>
      <Card className={classes.root}>
        <CardContent>
          <Typography className={classes.title} color="textSecondary">
            {activity.name}
          </Typography>
            <Grid item className={classes.duration}>
              <Typography variant="body2" component="p">
                Duration
              </Typography> <br/>
              <Typography variant="body2" component="p">
                {activity.duration}
              </Typography>
            </Grid>
            <Grid item className={classes.intensity}>
              <Typography variant="body2" component="p">
                Intensity
              </Typography> <br/>
              <Typography variant="body2" component="p">
                1/{activity.intensity}
              </Typography>
            </Grid>
        </CardContent>
        <CardActions>
          <Button size="small">Learn More</Button>
        </CardActions>
     </Card>
    </div>
  )
}
